summary.map:
function (doc, meta) {
  if(doc.type == 'project')
     emit(meta.id, {name: doc.name, desc: doc.desc});
  else if(doc.type == 'task') {
    var key = doc.project ? doc.project : 'none';
    emit(key, {status: doc.status ? doc.status : 'open'});
  }
}

summary.reduce:
function (keys, values, rereduce) {
  var out = {
    numOfTasks: 0,
    status: {}
  };
  for(var i = 0; i < values.length; i++) {
    var v = values[i];
    if(v.status) {
      out.numOfTasks++;
      var s = v.status;
      var numOfStatus = out.status[s] ? out.status[s] : 0;
      out.status[s] = ++numOfStatus;
    } else {
      out.name = v.name;
      out.desc = v.desc;
    }
  }
  return out;
}
